(function() {
    "use-strict";
    $(document).ready(function() {
        $(".disable-anchors a").click(function(a) {
            a.preventDefault();
        }), $("[data-href]").click(function() {
            var a, b, c, d;
            b = window.location.pathname.split("/"), a = b[b.length - 1], c = b[b.length - 2], 
            d = {}, d[c] = a, window.history.pushState(d, "", a), document.location.replace($(this).data("href"));
        });
    });
}).call(this);

/*MENU SCROLL*/

$(document).scroll(function() {    
  var scroll = $(this).scrollTop();
  if (scroll >= 458) {
      $(".header-scroll").addClass("active");
  } else {
      $(".header-scroll").removeClass("active");
  }
});

/*MODAL CONTACTO*/
$('.btn-contacto').click(function(){
    $('.modal-bg').fadeIn();
    $('#modal').fadeIn();
});
$('#close').click(function(){
    $('.modal-bg').fadeOut();   
    $('#modal').fadeOut();
  return false;
});

/*FUNCION DE POP UP GRACIAS*/
$(document).ready(function(){
  $("#close-send").click(function(){
      $(".modal-bg").fadeOut();
      $("#Modal-gracias").modal();
  });
});

/*MENU RESPONSIVE*/
$(".btn-mobile").click(function() {
  $("#overlay-menu").toggleClass("open");
});

/*COLLAPSE*/
$('span').on('click', function (event) {
  $(this).parent().parent().toggleClass('active').siblings().removeClass('active');
  event.preventDefault();
});
/*COLLAPSE CHANGE BUTTON*/
$('.change-arrow').click(function(){
  $(this).parent().find('#up').toggleClass("hidden");
  $(this).parent().find('#down').toggleClass("hidden"); 
});
